class ToggleLike{constructor(e){this.toggler=e,this.toggleLike()}toggleLike(){$(this.toggler).click((function(e){e.preventDefault();let t=this;console.log("insidetoggle like"),$.ajax({type:"POST",url:$(t).attr("href")}).done((function(e){let l=parseInt($(t).attr("data-likes"));console.log(l),1==e.data.deleted?l-=1:l+=1,$(t).attr("data-likes",l),$(t).html(`${l} Likes`)})).fail((function(e){console.log("error in completing the request")}))}))}}